#region Using Namespaces

using System;
using System.Collections.Generic;
using System.IO;
using System.Threading.Tasks;
using Imageflow.Fluent;

#endregion

namespace ComposeImages
{
    internal class Program
    {
        private static async Task Main(string[] args)
        {
            await Overlay("../../../images/blue_square.jpg", "../../../images/overlaid-on-jpg.png");
            await Overlay("../../../images/blue_square.png", "../../../images/overlaid-on-png.png");
        }

        public static async Task Overlay(string inPath, string outPath)
        {
            var canvas = new StreamSource(File.OpenRead(inPath), true);

            var overlays = new List<IBytesSource>
            {
                new BytesSource(Convert.FromBase64String(
                    @"")),
                new BytesSource(Convert.FromBase64String(
                    @"")),
                new BytesSource(Convert.FromBase64String(
                    @""))
            };

            using var job = new ImageJob();

            var start = job.Decode(canvas);

            foreach (var overlay in overlays)
            {
                start = start.Watermark(overlay, new WatermarkOptions()
                    .SetMarginsLayout(
                        new WatermarkMargins(WatermarkAlign.Image, 30, 30, 30, 30),
                        WatermarkConstraintMode.Within,
                        new ConstraintGravity(90, 90))
                    .SetOpacity(1f));
            }

            await using var file = File.OpenWrite(outPath);

            await start.EncodeToStream(file, false, new LodePngEncoder())
                .Finish()
                .InProcessAsync();
        }
    }
}